let timeoutRef = null
/**
 * This function sets a timeout and then returns either the data
 * passed in or an error. You can use this to replace an API call
 * to reduce the number of calls you make during testing.
 * @param {*} success - Whether you want the fake call to succeed (or throw Error).
 * @param {*} timeout - The length of time before you get a response
 * @param {*} dataToReturn - The data that will be returned when successful.
 * @returns - The data entered into the function
 */
export const makeFakeApiRequest = (success, timeout = 1000, dataToReturn) => {
  //This cancels the previous request if another one is made before it completes.
  if (timeoutRef !== null) {
    clearTimeout(timeoutRef)
    console.log(
      'You made multiple API requests at the same time. Did you mean to?'
    )
  }
  return new Promise((resolve, reject) => {
    timeoutRef = setTimeout(() => {
      if (success) {
        resolve(dataToReturn)
      } else {
        reject({ message: 'Fake Error' })
      }
    }, timeout)
  })
}
